select count(I.ICE_ID),STEP.LKP_CD_DESC as WRKFLW_STEP, STS.LKP_CD_DESC as WRKFLW_STS from  WRKFLW_DTL W
        left join INGS_BLOB_DTL I on W.BLOB_DTL_ID = I.BLOB_DTL_ID
        left join (select LKP_CD,LKP_CD_DESC from ICEe.VW_LKP_MAST_VAL where LKP_MAST_NM = 'WRKFLW_STEP_TYPE') STEP on W.WRKFLW_STEP_TYPE_ID = cast(STEP.LKP_CD as UNSIGNED INTEGER)
        left join (select LKP_CD,LKP_CD_DESC from ICEe.VW_LKP_MAST_VAL where LKP_MAST_NM = 'WRKFLW_STS_TYPE') STS on W.WRKFLW_STS_TYPE_ID = cast(STS.LKP_CD as UNSIGNED INTEGER)
        where
        W.CURR_IND =1 
        and 
    I.BLOB_DTL_ID in
    (97421366, 97421388, 97421377, 97421359, 97421367, 97421342, 97421220, 97421371, 97421343, 97421390, 97421387, 97421272, 97421394, 97421376, 97421393, 97421380, 97421375, 97421400, 97421384, 97421385, 97421378, 97421373, 97421117, 97421397, 97421381, 97421352, 97421361, 97421076, 97421368, 97421402, 97421331, 97421401, 97421398, 97421396, 97421395, 97421389, 97421404, 97421406, 97421405, 97421403, 97421414, 97421410, 97421409, 97421391, 97421399, 97421418, 97421417, 97421345, 97421415, 97421420, 97421416, 97421392, 97421419, 97421372, 97421425, 97421386, 97421374, 97421428, 97421423, 97421186, 97421411, 97421431, 97421440, 97421436, 97421445, 97421434, 97421424, 97421443, 97421439, 97421427, 97421437, 97421430, 97421448, 97421435, 97421382, 97421438, 97421433, 97421444, 97421454, 97421422, 97421453, 97421455, 97421451, 97421458, 97421446, 97421457, 97421429, 97421459, 97421449, 97421470, 97421452, 97421456, 97421474, 97421476, 97421461, 97421464, 97421471, 97421466, 97421478, 97421469)  group by WRKFLW_STEP,WRKFLW_STS;

